import React, { useState } from 'react'
import { Navigate } from 'react-router-dom';

const Login = () => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault(); // Ngăn chặn tải lại trang khi gửi form

    const body = {
      email: email,
      password: password
    };

    const options = {
      method: 'POST', // Sử dụng phương thức POST
      headers: {
        'Content-Type': 'application/json' // Định dạng dữ liệu gửi lên là JSON
      },
      body: JSON.stringify(body) // Chuyển đổi dữ liệu thành chuỗi JSON
    };

    try {
      const response = await fetch('http://localhost:3001/users/login', options)
      .then(response => response.json())
      .then(data => {
        console.log(data)
        localStorage.setItem('token', data.token)
        <Navigate
      })

    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div>
      <h1>Login</h1>
      <form>
        <div className="mb-3 mt-3">
          <label className="form-label">Email:</label>
          <input type="email" className="form-control" id="email" placeholder="Enter email" name="email" onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="mb-3">
          <label className="form-label">Password:</label>
          <input type="password" className="form-control" id="pwd" placeholder="Enter password" name="password" onChange={(e) => setPassword(e.target.value)} />
        </div>

        <button type="submit" className="btn btn-success" onClick={handleLogin}>Submit</button>
      </form>
    </div>
  )
}

export default Login
