import React, { useState, useEffect } from 'react';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(1);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const response = await fetch(`http://localhost:3001/products/page/${page}`);
        const data = await response.json();
        if (data.status === true) setProducts(data.data);
      } catch (error) {
        console.log('Get products error: ', error.message);
      }
    };
    getProducts();
  }, [page]);

  const nextPage = () => {
    setPage(page + 1);
  };

  const prevPage = () => {
    setPage(page - 1);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="navbar navbar-expand-md navbar-dark bg-success mb-4">
          {/* Navbar content */}
        </nav>

        <div id="manage-products" className="mt-5">
          <table className="table">
            <thead>
              <tr>
                {/* Table headers */}
              </tr>
            </thead>
            <tbody>
              {/* Table data */}
              {products.map((product) => (
                <tr key={product._id}>
                  {/* Table cells */}
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination buttons */}
          <div className="d-flex justify-content-between align-items-center">
            {page === 1 ? (
              <button className="btn btn-primary" onClick={nextPage}>Next Page</button>
            ) : (
              <>
                <button className="btn btn-primary" onClick={prevPage}>Previous Page</button>
                <span>Page {page}</span>
                <button className="btn btn-primary" onClick={nextPage}>Next Page</button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
