import React, { useState, useEffect } from 'react'
import ChartComponent from './ChartComponent'
const Home = () => {

  const [stransactionHistory, setStransactionHistory] = useState([]);
  const [sumWeek, setSumWeek] = useState([]);
  const [sumMonth, setSumMonth] = useState([]);
  const [dayOfWeek, setDayOfWeek] = useState([]);
  const [month, setMonth] = useState(second)

  useEffect(() => {
    const getTransaction = async () => {
      try {
        const result = await fetch('http://localhost:3001/transactions');
        const data = await result.json();
        if (data.status == true) setStransactionHistory(data.data);
        return null;
      } catch (error) {
        console.log('Get transaction error: ', error.message);
      }
    }
    getTransaction();
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">

        {/* <!-- Nav bar --> */}
        <nav className="navbar navbar-expand-md navbar-dark bg-success mb-4 ">
          <div className="container-fluid">
            <span className="navbar-brand" href="/">
              Dashboard
            </span>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarCollapse"
              aria-controls="navbarCollapse"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse " id="navbarCollapse">
              <ul className="navbar-nav me-auto mb-2 mb-md-0">
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="/home">
                    Thống kê
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/products">
                    Quản lý sản phẩm
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/users">
                    Quản lý người dùng
                  </a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/login">
                    Đăng xuất
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>



        {/* <!-- Statistics --> */}
        <div className="row">
          <div className="col-md-6">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Thống kê đầu tiên</h5>
                <ChartComponent />
              </div>
            </div>
          </div>
        </div>


      </div>
    </div>
  )
}

export default Home
