import React, { useState } from 'react'
import { Navigate, useNavigate } from 'react-router-dom';

const Login = () => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // Ngăn chặn tải lại trang khi gửi form

    const body = {
      email: email,
      password: password
    };

    const options = {
      method: 'POST', // Sử dụng phương thức POST
      headers: {
        'Content-Type': 'application/json' // Định dạng dữ liệu gửi lên là JSON
      },
      body: JSON.stringify(body) // Chuyển đổi dữ liệu thành chuỗi JSON
    };

    try {
      await fetch('http://localhost:3001/users/login', options)
        .then(response => response.json())
        .then(data => {
          console.log(data)
          localStorage.setItem('token', data.token)
          navigate('/')
        })
        .catch(error => console.log(error));

    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div class="container">
  <div class="row justify-content-center">
    <div class="col-xl-10 col-lg-12 col-md-9">
      <div class="card o-hidden border-0 shadow-lg my-5">
        <div class="card-body p-0">
          <div class="row">
            <div class="col-lg-6 d-none d-lg-block bg-login-image"></div>
            <div class="col-lg-6">
              <div class="p-5">
                <h1 class="h4 text-gray-900 mb-4 text-center">Welcome Back!</h1>
                <form class="user">
                  <div class="form-group">
                    <input type="email" class="form-control form-control-user" id="exampleInputEmail" aria-describedby="emailHelp" placeholder="Enter Email Address..." />
                  </div>
                  <div class="form-group">
                    <input type="password" class="form-control form-control-user" id="exampleInputPassword" placeholder="Password" />
                  </div>
                  <hr />
                  <a href="index.html" class="btn btn-primary btn-user btn-block">Login</a>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

  )
}

export default Login
